package pack1;

import java.util.Arrays;

public class M23 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] x = new int[2];
		x[0] = 100;
		x[1] = 200;
		System.out.println(Arrays.toString(x));
		text(x);
		System.out.println(Arrays.toString(x));
	}

	static void text(int[] x) {
		// TODO Auto-generated method stub
		x[0] = 1000;
		x[1] = 2000;
	}

}
